package javapackage;

public class Forloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      
		
		
       for (int i=20;i<=40;i++)
       {
    	 if(i%2 == 0) 
    	 {
    	  System.out.println("The number is even"+i);
    	 }
    	  else
    	  {
    		  System.out.println("The number is odd"+i);
    		  
    	 }	 
       }
	}

}
