package javapackage;

public class IfElseprogram {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     int a=40;
     
    if (a%2==0)
    {
    	System.out.println("The even number"+a);
    	
    }
    else
    	System.out.println("The odd number"+a);
	}

}
