package javapackage;

public class Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 int a=20;
 int b=10;
 int c;
  
   c= a+b;
    
    System.out.println(c);
   c=a-b;
    
    System.out.println(c); 
   c=a/b;
     
    System.out.println(c);
   c=a*b;
   
    System.out.println(c);
   c=a%b;
    System.out.println(c);
		   
    
		   
	}

}
